	when address = '6KxusedL87PBibr1t1f4ggzAyTAmWEPqSpqXbkdoybNwHVw5Nb7cUESBmQw5XK8TyvbQiueyqkR9XMNaUgpWx3jT54p' then 'Re-emissions Contract'
	when address = ''then 'SigUSD'
	when address = '4L1ktFSzm3SH1UioDuUf5hyaraHird4D2dEACwQ1qHGjSKtA6KaNvSzRCZXZGf9jkfNAEC1SrYaZmCuvb2BKiXk5zW9xuvrXFT7FdNe2KqbymiZvo5UQLAm5jQY8ZBRhTZ4AFtZa1UF5nd4aofwPiL7YkJuyiL5hDHMZL1ZnyL746tHmRYMjAhCgE7d698dRhkdSeVy' then 'Ergo Foundation'
	when address = '' then 'Spectrum Dex'
	when address = '' then 'SigUSD Bank'
	when address = '' then 'Skyharbor'
else ' '
end Contract,

round((sum(value/1000000))/1000,3) as Amount_ERG,
round(round((sum(value/1000000))/1000,3)* 1.464,2) as Amount_USD,
no.address

from node_outputs no

left join
(select ni.box_id
  from node_inputs ni
where ni.main_chain = true ) i
on i.box_id = no.box_id

where no.main_chain = true
and not exists (select box_id from node_inputs ni where no.box_id = ni.box_id)
and no.address not like '9%%'
and no.address not like '88%%'
group by no.address;